import "./App.css";
import React, { useReducer, useRef } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Login from "./pages/Login";
import CommunityList from "./pages/ CommuityList";
import Community_Create from "./pages/Community_Create";
import SignUp from "./pages/SignUp";

//reducer
const reducer = (state, action) => {
  let newState = [];
  switch (action.type) {
    case "INIT": {
      return action.data;
    }
    case "CREATE": {
      newState = [action.date, ...state];
      break;
    }
    case "REMOVE": {
      newState = state.filter((it) => it.id !== action.targetId);
      break;
    }
    case "EDIT": {
      newState = state.map((it) =>
        it.id === action.data.id ? { ...action.data } : it
      );
      break;
    }
    default:
      return state;
  }
  return newState;
};

export const DiaryStateContext = React.createContext();
export const DiaryDispatchContext = React.createContext();

const dummylist = [
  {
    id: 1,
    community_name: "지수그룹",
    community_picture: "이쁜사진",
    community_allow: "허용",
    create_date: new Date().getTime(),
  },
  {
    id: 2,
    community_name: "수연그룹",
    community_picture: "이쁜사진2",
    community_allow: "허용",
    create_date: new Date().getTime(),
  },
  {
    id: 3,
    community_name: "지원그룹",
    community_picture: "이쁜사진3",
    community_allow: "허용",
    create_date: new Date().getTime(),
  },
];

function App() {
  const [data, dispatch] = useReducer(reducer, dummydata);
  console.log(new Date().getTime());
  const dataId = useRef(0);
  //CREATE
  const onCreate = (date, content, emotion) => {
    dispatch({
      type: "CREATE",
      data: {
        id: dataId.current,
        date: new Date(date).getTime(),
        content,
        emotion,
      },
    });
    dataId.current += 1;
  };
  //REMOVE
  const onRemove = (targetId) => {
    dispatch({ type: "REMOVE", targetId });
  };
  //EDIT
  const onEdit = (targetId, date, content, emotion) => {
    dispatch({
      type: "EDIT",
      data: {
        id: targetId,
        date: new Date(date).getTime(),
        content,
        emotion,
      },
    });
  };
  return (
    <DiaryStateContext.Provider value={data}>
      <DiaryDispatchContext.Provider value={{ onCreate, onRemove, onEdit }}>
        <BrowserRouter>
          <div className="App">
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/signUp" element={<SignUp />} />
              <Route path="/communitylist" element={<CommunityList />} />
              <Route path="/community_create" element={<Community_Create />} />
            </Routes>
          </div>
        </BrowserRouter>
      </DiaryDispatchContext.Provider>
    </DiaryStateContext.Provider>
  );
}

export default App;
