import React, { useState, useRef, useContext, useEffect } from "react";
import { useNavigate, useParams, useSearchParams } from "react-router-dom";

import { CommunityDispatchContext } from "./../App";
function ChallengeEditor({ originData, isEdit }) {
  const navigate = useNavigate();
  const nameRef = useRef();
  const { communityNumber } = useParams();
  const [state, setState] = useState({
    challenge_name: "",
    challenge_mode: "보물찾기",
  });
  const date = new Date().getTime();

  const { onCreate_ch, onEdit_ch, onRemove_ch } = useContext(
    ChallengeDispatchContext
  );
  const handleChangeState = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    if (state.challenge_name < 1) {
      nameRef.current.focus();
      return;
    }
    if (
      window.confirm(
        isEdit
          ? "커뮤니티를 수정하시겠습니까?"
          : "새로운 커뮤니티를 생성하시겠습니까?"
      )
    ) {
      if (isEdit) {
        onEdit_ch(
          originData.id,
          date,
          state.challenge_name,
          state.challenge_mode,
          state.community_picture
        );
      } else {
        onCreate_ch(
          date,
          state.challenge_name,
          state.challenge_mode,
          state.community_picture
        );
      }
    }
    navigate("/communitylist", { replace: true });
  };
  //이전 입력값 불러오기
  useEffect(() => {
    if (isEdit) {
      setState({
        challenge_name: originData.challenge_name,
        challenge_mode: originData.challenge_mode,
        community_picture: originData.community_picture,
      });
    }
  }, [isEdit, originData]);

  const handleRemove = () => {
    if (window.confirm("정말 삭제하시겠습니까?")) {
      onRemove_ch(originData.id);
      navigate("/communitylist", { replace: true });
    }
  };
  return (
    <div>
      <h2>챌린지 생성 페이지</h2>
      <section>
        <h4>1. 챌린지 이름</h4>
        <div className="input_box">
          <input
            placeholder="당신의 챌린지 이름은 무엇인가요?"
            ref={nameRef}
            name="challenge_name"
            value={state.challenge_name}
            onChange={handleChangeState}
          />
        </div>
      </section>

      <section>
        <div>
          <h4>2. 챌린지 모드</h4>
          <select
            name="challenge_mode"
            value={state.challenge_mode}
            onChange={handleChangeState}
          >
            <option value={"보물찾기"}>보물찾기</option>
            <option value={"집중모드"}>집중모드</option>
          </select>
        </div>
      </section>
      <div className="control_box">
        <div>
          <button
            onClick={() => {
              navigate(`/community/${communityNumber}`, { replace: true });
            }}
          >
            {isEdit ? "수정취소" : "취소하기"}
          </button>
          {isEdit && <button onClick={handleRemove}>삭제하기</button>}
        </div>
        <button onClick={handleSubmit}>
          {" "}
          {isEdit ? "수정완료" : "작성완료"}
        </button>
      </div>
    </div>
  );
}

export default ChallengeEditor;
