import React, { useState, useRef, useContext, useEffect } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";

function CommunityEditor({ isEdit }) {
  const navigate = useNavigate();
  const nameRef = useRef();
  const [state, setState] = useState({
    community_name: "",
    community_picture: "",
    community_allow: "허용",
  });
  const handleChangeState = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };
  return (
    <div>
      <h2>커뮤니티 생성 페이지</h2>
      <section>
        <h4>1. 커뮤니티 이름</h4>
        <div className="input_box">
          <input
            placeholder="당신의 커뮤니티 이름은 무엇인가요?"
            ref={nameRef}
            name="community_name"
            value={state.community_name}
            onChange={handleChangeState}
          />
        </div>
      </section>
      <section>
        <h4>2. 커뮤니티 사진 등록</h4>
        <div>
          <input
            name="community_picture"
            value={state.community_picture}
            onChange={handleChangeState}
          />
        </div>
      </section>
      <section>
        <div>
          <h4>3. 커뮤니티 결제 허용 여부</h4>
          <select
            name="community_allow"
            value={state.community_allow}
            onChange={handleChangeState}
          >
            <option value={"허용"}>허용</option>
            <option value={"거부"}>거부</option>
          </select>
        </div>
      </section>
    </div>
  );
}

export default CommunityEditor;
