import { useNavigate, useParams } from "react-router-dom";
import { useState, useRef, useContext } from "react";

import CommunityEditor from "../components/CommunityEditor";
import { CommunityDispatchContext } from "./../App";

const Edit_Comu = () => {
  const date = new Date().getTime();
  //    네비게이션
  const navigate = useNavigate();

  // 미 입력시 알림
  const nameRef = useRef();
  // 입력 정보 처리
  const [state, setState] = useState({
    community_name: "",
    community_picture: "",
    community_allow: "허용",
  });

  const { onCreate } = useContext(CommunityDispatchContext);
  const handleChangeState = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    if (state.community_name < 1) {
      nameRef.current.focus();
      return;
    }
    onCreate(
      date,
      state.community_name,
      state.community_allow,
      state.community_picture
    );
    alert("저장성공");
    navigate("/communityHome", { replace: true });
  };
  const { communityNumber } = useParams();

  console.log(communityNumber);
  return (
    <div>
      <div>
        <button
          onClick={() => {
            navigate(-1);
            return;
          }}
        >
          수정 취소
        </button>
      </div>
      <div>여기는 커뮤니티{communityNumber} 수정 페이지 입니다.</div>
    </div>
  );
};

export default Edit_Comu;
