import "./App.css";
import { BrowserRouter, Routes, Route, useParams } from "react-router-dom";
import React, { useReducer, useRef } from "react";

import Login from "./pages/Login";
import CommunityList from "./pages/ CommuityList";
import Community_Create from "./pages/Community_Create";
import SignUp from "./pages/SignUp";
import Community from "./pages/Community";

const dummylist = [
  {
    id: 1,
    community_name: "지수그룹",
    community_picture: "이쁜사진",
    community_allow: "허용",
    create_date: new Date().getTime(),
  },
  {
    id: 2,
    community_name: "수연그룹",
    community_picture: "이쁜사진2",
    community_allow: "허용",
    create_date: new Date().getTime(),
  },
  {
    id: 3,
    community_name: "지원그룹",
    community_picture: "이쁜사진3",
    community_allow: "허용",
    create_date: new Date().getTime(),
  },
];

const reducer = (state, action) => {
  let newState = [];
  switch (action.type) {
    case "INIT": {
      return action.data;
    }
    case "CREATE": {
      newState = [action.data, ...state];
      break;
    }
    case "REMOVE": {
      newState = state.filter((it) => it.id !== action.targetId);
      break;
    }
    case "EDIT": {
      newState = state.map((it) =>
        it.id === action.data.id ? { ...action.data } : it
      );
      break;
    }
    default:
      return state;
  }
  return newState;
};

export const CommunityStateContext = React.createContext();
function App() {
  const [data, dispatch] = useReducer(reducer, []);

  const dataID = useRef(0);
  //CREATE
  const onCreate = (
    date,
    community_name,
    community_allow,
    community_picture
  ) => {
    dispatch({
      type: "CREATE",
      data: {
        id: dataID.current,
        date: new Date(date).getTime(),
        community_name,
        community_picture,
        community_allow,
      },
    });
    dataID.current += 1;
  };
  //REMOVE
  const onRemove = (targetId) => {
    dispatch({ type: "REMOVE", targetId });
  };
  //EDIT
  const onEdit = (
    targetId,
    date,
    community_name,
    community_allow,
    community_picture
  ) => {
    dispatch({
      type: "EDIT",
      data: {
        id: targetId,
        date: new Date(date).getTime(),
        community_name,
        community_allow,
        community_picture,
      },
    });
  };

  return (
    <CommunityStateContext.Provider value={data}>
      <BrowserRouter>
        <div className="App">
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/signUp" element={<SignUp />} />
            <Route path="/communitylist" element={<CommunityList />} />
            <Route
              path="/communitylist/:communityNumber"
              element={<Community />}
            />
            <Route path="/community_create" element={<Community_Create />} />
          </Routes>
        </div>
      </BrowserRouter>
    </CommunityStateContext.Provider>
  );
}

export default App;
