import "./App.css";
import { BrowserRouter, Routes, Route, useParams } from "react-router-dom";
import React, { useReducer, useRef } from "react";

import Login from "./pages/Login";
import CommunityHome from "./pages/ CommuityHome";
import Community_Create from "./pages/Community_Create";
import SignUp from "./pages/SignUp";
import Community from "./pages/Community";
import Edit_Comu from "./pages/Edit_Comu";

//community reducer
const reducer = (state, action) => {
  let newState = [];
  switch (action.type) {
    case "INIT": {
      return action.data;
    }
    case "CREATE": {
      const newItem = {
        ...action.data,
      };
      newState = [...state, newItem];
      break;
    }
    case "REMOVE": {
      newState = state.filter((it) => it.id !== action.targetId);
      break;
    }
    case "EDIT": {
      newState = state.map((it) =>
        it.id === action.data.id ? { ...action.data } : it
      );
      break;
    }
    default:
      return state;
  }
  return newState;
};

//chanllenge reducer
const reducer2 = (state2, action2) => {
  let newState2 = [];
  switch (action2.type) {
    case "INIT": {
      return action2.data2;
    }
    case "CREATE": {
      const newItem2 = {
        ...action2.data2,
      };
      newState2 = [...state2, newItem2];
      break;
    }
    case "REMOVE": {
      newState2 = state2.filter((it) => it.id2 !== action2.targetId2);
      break;
    }
    case "EDIT": {
      newState2 = state2.map((it) =>
        it.id2 === action2.data2.id2 ? { ...action2.data2 } : it
      );
      break;
    }
    default:
      return state2;
  }
  return newState2;
};

export const CommunityStateContext = React.createContext();
export const CommunityDispatchContext = React.createContext();
function App() {
  //community reducer
  const [data, dispatch] = useReducer(reducer, []);
  const dataId = useRef(0);
  //CREATE
  const onCreate_comu = (
    date,
    community_name,
    community_allow,
    community_picture
  ) => {
    dispatch({
      type: "CREATE",
      data: {
        id: dataId.current,
        date: new Date(date).getTime(),
        community_name,
        community_picture,
        community_allow,
      },
    });
    dataId.current += 1;
  };
  //REMOVE
  const onRemove_comu = (targetId) => {
    dispatch({ type: "REMOVE", targetId });
  };
  //EDIT
  const onEdit_comu = (
    targetId,
    date,
    community_name,
    community_allow,
    community_picture
  ) => {
    dispatch({
      type: "EDIT",
      data: {
        id: targetId,
        date: new Date(date).getTime(),
        community_name,
        community_allow,
        community_picture,
      },
    });
  };

  //challenge reducer
  const [data2, dispatch2] = useReducer(reducer2, []);
  const dataId2 = useRef(0);
  //create
  const onCreate_ch = (date2, ch_name, ch_mode) => {
    dispatch2({
      type: "CREATE",
      data2: {
        id2: dataId2.current,
        date2: new Date(date2).getTime(),
        ch_name,
        ch_mode,
      },
    });
    dataId2.current += 1;
  };
  //REMOVE
  const onRemove_ch = (targetId2) => {
    dispatch({ type: "REMOVE", targetId2 });
  };
  //EDIT
  const onEidt_ch = (targetId2, date2, ch_name, ch_mode) => {
    dispatch2({
      type: "EDIT",
      data2: {
        id2: targetId2,
        date2: new Date(date2).getTime(),
        ch_name,
        ch_mode,
      },
    });
  };
  return (
    <CommunityStateContext.Provider value={(data, data2)}>
      <CommunityDispatchContext.Provider
        value={{ onCreate_comu, onEdit_comu, onRemove_comu }}
      >
        <BrowserRouter>
          <div className="App">
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/signUp" element={<SignUp />} />
              <Route path="/communityhome" element={<CommunityHome />} />
              <Route
                path="/communityhome/:communityNumber"
                element={<Community />}
              />
              <Route path="/community_create" element={<Community_Create />} />
              <Route
                path="/edit_comu/:communityNumber"
                element={<Edit_Comu />}
              />
            </Routes>
          </div>
        </BrowserRouter>
      </CommunityDispatchContext.Provider>
    </CommunityStateContext.Provider>
  );
}

export default App;
