      navigate("/CommunityHome", { replace: true });
    }
  };

  return (
    <div>
      <button
        onClick={() => {
          navigate(-1);
        }}
      >
        {isEdit ? "수정취소" : "뒤로가기"}
      </button>
      <p>여기는 챌린지 생성 페이지 입니다.</p>
    </div>
  );
};

export default ChallengeEditor;
